declare module "*.svg" {
  /**
   * Use `any` to avoid conflicts with
   * `@svgr/webpack` plugin or
   * `babel-plugin-inline-react-svg` plugin.
   */
  const content: {
    src: string;
    width: number;
    height: number;
    blurWidth: number;
    blurHeight: number;
  };

  export default content;
}
